var regEx = /\.([^.]*)$/;
var reqParams = request_vendor_parameters;
var finalReqParams={};
if (reqParams!==null && reqParams['fields'] !== null && reqParams['fields'] !== undefined) {
 var fields = reqParams['fields'];
 var split = fields.trim().split(',');
 var finalFields = '';
 for (var str of split) {
   if(str.includes('.'))
   {
   finalFields += str.replace(regEx, ':' + '$1') + ';';
   }
   else
   {
     finalFields +=str+',';
   }
 }
 finalReqParams['fields'] = finalFields;
}
if (reqParams!==null && reqParams['orderBy'] !== null && reqParams['orderBy'] !== undefined){
  var orderBy=reqParams['orderBy'];
  orderBy= orderBy.trim().split(' ').join(':');
  finalReqParams['orderBy']=orderBy;
}

done({
 'request_vendor_parameters': finalReqParams
})

if (!response_iserror) {
 var responseBody = response_body;
 var itemsList = responseBody.items;
 var split, empUniqueId;
 for (var i = 0; i < itemsList.length; i++) {
  if (itemsList[i] !== 'undefined' && itemsList[i].links[0] !== 'undefined' && itemsList[i].links[0].href !== 'undefined') {
   split = itemsList[i].links[0].href.split("/");
   empUniqueId = split[split.length - 1];
   itemsList[i]["id"] = empUniqueId;
  }
 }
 responseBody.items = null;
 responseBody.items = itemsList;
}

done({
 'response_body': responseBody
});

