var modifiedUrl = request_vendor_path;

if (request_path_variables.objectName === 'employees') {
 modifiedUrl = request_vendor_path.replace('employees', 'emps');
}
else if (request_path_variables.objectName === 'employeesRoles') {
 modifiedUrl = request_vendor_path.replace('employeesRoles', 'emps');
}
else if (request_path_variables.objectName === 'requisitions') {
 modifiedUrl = request_vendor_path.replace('requisitions', 'jobs');
}
else if (request_path_variables.objectName === 'timeCards') {
 modifiedUrl = request_vendor_path.replace('timeCards', 'timeRecords');
}
else if (request_path_variables.objectName === 'timeEvents') {
 modifiedUrl = request_vendor_path.replace('timeEvents', 'timeEventRequests');
}

done({
 'request_vendor_path': modifiedUrl
});

if (!response_iserror) {
 var tempObjectName = request_path_variables.objectName;
 if (tempObjectName === 'employees' || tempObjectName === 'employeesRoles') {
  request_path_variables['objectName'] = 'emps';
 }
 if (tempObjectName === 'timeEvents') {
  request_path_variables['objectName'] = 'timeEventRequests';
 }
 if (tempObjectName === 'timeCards') {
  request_path_variables['objectName'] = 'timeRecords';
 }
  if (tempObjectName === 'requisitions') {
  request_path_variables['objectName'] = 'jobs';
 }
 var objname = request_path_variables.objectName;
 var attributesList;
 if (tempObjectName === 'employeesRoles') {
  attributesList = response_body[objname].children.roles.attributes;
 } else {
  attributesList = response_body[objname].attributes;
 }

 var objectList = [];

 for (var i = 0; i < attributesList.length; i++) {
  var metaData = {};

  metaData["path"] = attributesList[i].name;
  metaData["vendorPath"] = attributesList[i].name;
  metaData["displayName"] = attributesList[i].title;
  metaData["vendorDisplayName"] = attributesList[i].title;
  metaData["vendorNativeType"] = attributesList[i].type;
  metaData["vendorRequired"] = attributesList[i].mandatory;
  metaData["required"] = attributesList[i].mandatory;
  metaData["filterable"] = attributesList[i].queryable;
  metaData["updateable"] = attributesList[i].updatable;
  metaData["precision"] = attributesList[i].precision;
  metaData["length"] = attributesList[i].maxLength;

  objectList.push(metaData);
 }
 var finalList = {};
 finalList["fields"] = objectList;
}

done({
 'response_body': finalList,
 metadata_merge:true
});
