hooks.body:
let headers = response_headers;
let resBody=response_body;

if(request_path) {
  let pathComponents = request_path.split('/');
  let lastComponent = pathComponents[pathComponents.length -1];

  //Even on GET by ID, POST and PUT APIs the response is in array format
  //Normalizing the response
  if(request_method === 'POST' || request_method === 'PUT'
  || (request_method === 'GET' && !isNaN(lastComponent))
    ||request_path.indexOf('bulk')>=0){ 
     if(resBody && resBody.constructor === Array) {
      resBody = resBody[0];
    }
  }
}
let responseCode = response_status_code;

if (resBody && (resBody.code == 'INVALID_DATA' || resBody.code == 'MANDATORY_NOT_FOUND')) {
  responseCode= 400;
}

if(request_method === 'POST' || request_method === 'PUT'){
resBody["id"]= resBody.details.id;
}


//Vendor returns response body on Delete. Need to delete that on the Element API response
if(request_method === 'DELETE' && !response_iserror){
	resBody = {};
}

//The content-disposition header causes the  API  responses to be converted to file download link instead of JSON
//Hence, need to remove the header
if(headers && headers['Content-Disposition']) {
	delete headers['Content-Disposition'];
}

done({
  response_headers: headers,
  response_body: resBody,
  response_status_code:responseCode
});

let vendorPath = request_vendor_path;
let vendorReq = request_vendor_body;

//For GET all APIs the vendor path needs to be appended with /search if there is a where filter
if (request_method === 'GET' && (request_path.indexOf('users') < 0)) {
    if (request_expression) {
        vendorPath = vendorPath + '/search';
    }
}
//Converting the normalized element API request body to vendor format
if (request_path.indexOf('bulk') < 0 && (request_method === 'POST' || request_method === 'PUT')) {
    let reqBody = {};
    let dataArr = [];
    dataArr.push(JSON.parse(vendorReq));
    reqBody.trigger = [];
    reqBody.data = dataArr;
    vendorReq = reqBody;
}

//The below code converts the where expresstion to a format accepted by the vendor 
// if the query is on phone, email or word, it is passed on as it is
//If the query is on any other attribute of the object, it has to be converted to a format as: "{attribute_name}:equals|starts_with:{attribute_value}"
let criteria = [];
let vendorParams = request_vendor_parameters;
if (request_expression && (request_path.indexOf('users') < 0)) {

    for (var i = 0; i < request_expression.length; i++) {
        if (request_expression[i] && request_expression[i].value.indexOf('%') > 0) {
            criteria.push(request_expression[i].attribute + ':starts_with:' + request_expression[i].value.replace('%', ''));

        } else {
            if (request_expression[i].attribute != 'word' &&
                request_expression[i].attribute != 'phone' &&
                request_expression[i].attribute != 'email') {
                criteria.push(request_expression[i].attribute + ':equals:' + request_expression[i].value);
            } else {
                vendorParams[request_expression[i].attribute] = request_expression[i].value;
            }
        }
    }
    if (criteria) {
        vendorParams.criteria = '';
        for (var i = 0; i < criteria.length; i++) {
            if (vendorParams.criteria.length > 0) {
                vendorParams.criteria = vendorParams.criteria + ' and '
            }
            vendorParams.criteria = vendorParams.criteria + criteria[i];
        }

    }
}

done({
    continue: true,
    request_vendor_path: vendorPath,
    request_vendor_body: vendorReq,
    request_vendor_parameters: vendorParams
});

let modules = response_body_raw_map && response_body_raw_map['modules'];
let api_names = [];
for(let i=0;i<(modules && modules.length); i++) {
  let module = modules[i];
  if (module && module['api_supported']) {
    api_names.push(module['api_name']);
  }
}
done({
  response_body: api_names, 
  merge_objects: true
});

let body = {};
let actualData = response_body;
let actualFields = actualData && actualData.fields;
let fields = [];
for (var i=0; i < (actualFields && actualFields.length); i++) {
  let singleData = actualFields[i];
	let metadataFieldsObj = {};
	metadataFieldsObj.type = singleData['data_type'];
	metadataFieldsObj.vendorPath = singleData['api_name'];
	metadataFieldsObj.path = singleData['api_name'];
  metadataFieldsObj.displayName = singleData['field_label'];
  metadataFieldsObj.vendorDisplayName = singleData['field_label'];
  metadataFieldsObj.custom = singleData['custom_field'];
  metadataFieldsObj.updateable = !singleData['read_only'];
	fields.push(metadataFieldsObj);
}
body['fields'] = fields;

done({
  response_body: body
});

if (!response_iserror) {
  var response = {};  
  response['endpoint'] = 'zohocrmv2';
  response['dateTime'] = new Date();
  done({
    "response_body": response
  });
}

done({
  "response_body": "response",
  "continue":true
})
