var finalFieldsParam = "";
var vendor_params = request_vendor_parameters;
if (request_parameters == null) {
    done({
        "continue": false,
        "request_vendor_parameters": vendor_params
    })
}
if (request_method === 'GET') {
    if (request_parameters['fields'] === null || request_parameters['fields'] === undefined) {
        if (!vendor_params) {
            vendor_params = {};
        }
        vendor_params['fields'] = '*';
    } else {
        var fields = request_parameters['fields'];
        if (fields.includes('.')) {
            var fieldsList = fields.split(",");
            var nestedFieldsMap = new Map();
            var level1FieldsArr = [];
            fieldsList.forEach(function(fld) {
                var fldArr = fld.split(".");
                if (fldArr.length === 2) {
                    var fldKey = fldArr[0].trim();
                    var fldValue = fldArr[1].trim();
                    if (nestedFieldsMap.get(fldKey) !== undefined) {
                        var value = nestedFieldsMap.get(fldKey);
                        value = value + "," + fldValue;
                        nestedFieldsMap.set(fldKey, value);
                    } else {
                        nestedFieldsMap.set(fldKey, fldValue);
                    }
                } else {
                    level1FieldsArr.push(fld);
                }
            });

            level1FieldsArr.forEach(function(fld) {
                if (finalFieldsParam.length > 0) {
                    finalFieldsParam = finalFieldsParam + ",";
                }
                finalFieldsParam = finalFieldsParam + fld;
            });

            for (let key of nestedFieldsMap.keys()) {
                var flds = nestedFieldsMap.get(key);
                if (finalFieldsParam.length > 0) {
                    finalFieldsParam = finalFieldsParam + ",";
                }
                finalFieldsParam = finalFieldsParam + key + "(" + flds + ")";
            }
        } else {
            finalFieldsParam = fields;
        }
        vendor_params['fields'] = finalFieldsParam;
    }
}
done({
    "continue": true,
    "request_vendor_parameters": vendor_params
});

function converter(expression) {
	switch (expression) {
  	case '=':
    	return ':';
    default:
    	return expression;
  }	
}

function hasOr(params) {
	if (params.indexOf('OR') > -1 || params.indexOf('or') > -1 || params.indexOf('Or') > -1)  {
		return true;
	}
	return false;
}

function hasAnd(params) {
	if (params.indexOf('AND') > -1 || params.indexOf('and') > -1 || params.indexOf('And') > -1)  {
		return true;
	}
	return false;
}

// check whether date is valid
function fixDateValues(input) {
	let t = new Date(input);
	let finalResult = "";
	//is this a date?
	if (!isNaN(t.valueOf())) {
		if(input.indexOf(".") > -1)  {
  			finalResult = input.substr(0, input.lastIndexOf("."));
  		} else {
  			finalResult = input;
  		}
  		finalResult = finalResult.replace("-", "");
		finalResult = finalResult.replace("-", "");
		//to fit the format YYmmDDTHH:mm:ss
	} else {
		//not a date
		finalResult = input;
	}
	return finalResult;
}

function buildQuery(expression) {
	let fieldName = expression.attribute;
  	let value = expression.value;
  	let op = expression.operator;
  	value = fixDateValues(value);
	if (op === '<=') {
	    return  fieldName + ":[0 TO "+ value +"]";
  	} else if (op === '>=') {
  		if (value.indexOf("T") > -1) {
  			return  fieldName + ":["+ value +" TO *]";
  		} else {
  			return  fieldName + ":["+ value +" TO *]";	
  		}
	} else if (op === '<') {
  		return  fieldName + ":{0 TO "+ value +"}";
  	} else if (op === '>') {
  		if (value.indexOf("T") > -1) {
  			return  fieldName + ":{"+ value +" TO *}";
  		} else {
  			return  fieldName + ":{"+ value +" TO *}";	
  		}
	}else if(op ==='IN'){
	    return getInQueryTransform(fieldName, value);
	} else {
  		return fieldName + ' ' + converter(op) + ' ' + value;
  	}
}

function getInQueryTransform(fieldName, value){
    var output='';
	  var inArray = value.substring(0,value.length).split(",");
    for(var i = 0; i < inArray.length-1; i++) {
         output+=fieldName+":" +inArray[i] +" OR ";
    }
    output+=fieldName+":" +inArray[inArray.length-1];
    return output;
}

let map = {};
let query = "";
let baseUrl = configuration.baseUrl;

let path = baseUrl + '/search/Candidate';

if (request_parameters['where'] && request_expression) {
	let expressionsArray = request_expression;
	for (let i = 0; i < expressionsArray.length; i++) {
		let exp = expressionsArray[i];
		if (hasOr(request_parameters['where']) && i > 0)  {
		 	query += ' OR ' + buildQuery(exp);
		 } else if (hasAnd(request_parameters['where']) && i > 0)  {
		 	query += ' AND ' + buildQuery


		 	(exp);
		 } else {
			query += buildQuery(exp); 	
		 }
	}
	map.query = query;
} else {
	map.query = 'dateLastModified:{0 TO *}';
}

if ((request_parameters['sort'] === null || request_parameters['sort'] === undefined) &&(request_parameters['orderBy'] === null || request_parameters['orderBy'] === undefined)) {
	map.sort = '-dateLastModified';
}

if (request_parameters['fields'] === null || request_parameters['fields'] === undefined) {
	map.fields = '*';
} 

if (request_parameters['includeDeleted'] === null 
	|| request_parameters['includeDeleted'] === undefined
	|| request_parameters['includeDeleted'] === 'false') {
		map.query = '(' + map.query + ') AND isDeleted:false';
} 

done({
	"request_vendor_parameters": map,
	"request_vendor_path": path
});
