var formattedEvents = [];
var objType = events['objectType'];
var rawData = events[objType];

for (var i = 0; i < rawData.length; i++) {
    var eventObj = {};
    eventObj.event_type = "CREATED";
    eventObj.event_object_type = objType;
    if (events['objectType'] === "comments" 
    	|| events['objectType'] === "likes") {
        eventObj.event_object_id = rawData[i].id;
    } else if (events['objectType'] === "updates") {
        eventObj.event_object_id = rawData[i].updateKey;
    }
    formattedEvents.push(eventObj);
}

done({
    "events": formattedEvents
});

