  send.configuration["netsuite.accountId"] = configuration["netsuite.accountId"];
  send.configuration.consumer_key = configuration.consumer_key;
  send.configuration.consumer_secret = configuration.consumer_secret;
  send.configuration.token_id = configuration.token_id;
  send.configuration.token_secret = configuration.token_secret;
} else {
  throw("Invalid Authentication Type. Please enter either 'custom' or 'basic'.");
}

