var userNoOrg = configuration.username;

if (userNoOrg.indexOf("@") > -1) {
	var array = userNoOrg.split("@");
	userNoOrg = array[0] + "@" + array[1];
}

var headers = {
    "Authorization" : "Basic " + base64Encode(userNoOrg+":"+configuration.password)
}
return {
    "request_vendor_headers": headers,
    "continue": true
}

var body = request_vendor_body;
var vApp = null;
if (request_expression !== null) {
	var names = null;
	for (var i = 0; i < request_expression.length; i++) {
		print(request_expression[i]);
		if (request_expression[i].attribute === 'vApp') {
			vApp = request_expression[i].value;
		} 
		if (request_expression[i].attribute === 'metricName') {
			names = request_expression[i].value;
		}
	}
	if (names !== null) {
		if (names.indexOf(",") > -1) {
			//do comma stuff
		} else {
			body = body.replace("{metricNames}", "<MetricPattern>" + names + "</MetricPattern>");
		}
	} else {
		body = body.replace("{metricNames}", "");
	}
}

var headers = {
    "Content-Type" : "application/vnd.vmware.vcloud.metrics.historicUsageSpec+xml",
    "x-vcloud-authorization": configuration.xvcloudauthorization,
    "Accept": "application/*+xml;version=9.0"
}

return {  
    "request_vendor_path": "vApp/" + vApp + "/metrics/historic",
    "request_vendor_headers": headers,
    "request_vendor_body": body,
    "continue": true 
};

var newPath = "https://vca.vmware.com/api/metering/service-instance/" +request_parameters.server + "/l2/" + request_parameters.vdc + "/billable-usage";

if (request_parameters.startTime !== null && request_parameters.endTime !== null) {
	newPath += "?start=" + request_parameters.startTime + "&end=" + request_parameters.endTime;
}

var headers = {
    "Authorization" : "Bearer " + configuration.vchsauthorization
}
return {
    "request_vendor_headers": headers,
    "request_vendor_path": newPath,
    "continue": true 
}
