const _ = require('lodash');
var res_body = request_body;
var resBody = response_body;
var resp = {};
resp.request = res_body;
var workItemId = response_body['id'];
resp.response = resBody['fields'];
var body = resp
var req_map = JSON.parse(body.request);
var res_map = body.response;
var res_keys = Object.keys(res_map);
var req_keys = Object.keys(req_map);
var result = _.differenceWith(req_keys, res_keys);
const noMatches = _.filter(result);
var version = configuration['version'];
var apiV = request_parameters.version;
if (apiV) {
    var apiversion = apiV;
} else {
    var apiversion = version + '-preview.3';
}
var build_resp = [];
for (let s of req_keys) {
    if (noMatches.includes(s)) {
        var rslk = {
            "op": "add",
            "path": "/fields/" + s,
            "value": req_map[s]
        }
    } else {
        var rslk = {
            "op": "replace",
            "path": "/fields/" + s,
            "value": req_map[s]
        }
    }
    build_resp.push(rslk);
}
var wId = {
    "id": workItemId
}
build_resp.push(wId);
done({
    response_body: JSON.stringify(build_resp),
    request_vendor_parameters: {
        'api-version': apiversion
    }
})

